import React from 'react';
import Modal from 'react-bootstrap/Modal'
import Button from 'react-bootstrap/Button'
import Form from 'react-bootstrap/Form'

class MyVerticallyCenteredModal extends React.Component {
  constructor(props) {
    super(props);
    this.state = {userName:'', password:''}
    this.handleChange1 = this.handleChange1.bind(this);
    this.handleChange2 = this.handleChange2.bind(this);
  }

  handleChange1(e) {
  this.setState({userName: e.target.value})
 }
 handleChange2(e) {
  this.setState({password: e.target.value})
 }

render() { 
  return (
    <Modal
      {...this.props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          Enter Details 
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
      <Form className = 'form'>
        <Form.Group controlId="formBasicEmail" className = 'form-group'>
          <Form.Label style={{fontWeight:'bold'}}>Username</Form.Label>
          <Form.Text className="text-muted" htmlFor="username"></Form.Text>
          <Form.Control type="email" placeholder="Username" onChange={this.handleChange1}/>
        </Form.Group>
        <Form.Group controlId="formBasicPassword" className = 'form-group'>
        <Form.Label style={{fontWeight:'bold'}}>Password</Form.Label>
        <Form.Text className="text-muted" htmlFor="password"></Form.Text>
        <Form.Control type="password"  placeholder="Password" onChange={this.handleChange2}/>
          <br></br>
          <span style={{color:'red'}}>{this.state.txt}</span>
        </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button onClick={this.props.onHide}>Close</Button>
      </Modal.Footer>
    </Modal>
  );
}
}

export default MyVerticallyCenteredModal;
